    /**
     * 发送刷步数请求
     * @param username 用户名手机号
     * @param password 密码
     * @param steps    步数
     * @return
     */
    public static Result sport(String username, String password, String steps) {
        String[] login = login(username, password);
        if (login == null){
            return Result.error(ErrorCodeConstant.PARAMETER_ERROR,"账号密码错误，请重试");
        }
        String t = getTime();
        String dataJson = dataJson(DateUtil.today(), steps);
        String appToken = getAppToken(login[1]);
        String bodys = StrFormatter.format("userid={}&last_sync_data_time=1597306380&device_type=0&last_deviceid=DA932FFFFE8816E7&data_json={}", login[2], dataJson);
        String resStr = HttpRequest.post("https://api-mifit-cn.huami.com/v1/data/band_data.json?&t=" + t)
                .header("apptoken", appToken)
                .header("Content-Type", "application/x-www-form-urlencoded")
                .body(bodys)
                .execute().body();
        if (StringUtils.isEmpty(resStr)){
            return Result.error(ErrorCodeConstant.SERVICE_ERROR,"服务异常,修改失败，请联系管理员或者稍后重试");
        }
        JSONObject res = JSONObject.parseObject(resStr);
        if (res.getInteger("code") ==1 && res.getString("message").equals("success")){
            Result.success();
        }
        return Result.error(ErrorCodeConstant.SERVICE_ERROR,"服务异常,修改失败，请联系管理员或者稍后重试");
    }
